commits.content:
body {
  background: #000;
}

.sidebar {
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  padding: 12px;
  width: 300px;
}

.sidebar_navigation, .biblioteca {
  background-color: #121212;
  border-radius: 8px;
  padding: 16px 0 0 16px;
  color: #b3b3b3;
  font-family: "DM Sans", sans-serif;
}

.sidebar_navigation .logo {
  background: #121212;
  display: flex;
  border-radius: 8px;
  padding: 16px 0 0 16px;
}

.sidebar_navigation .logo img {
  width: 80px;
}

.sidebar nav ul {
  margin-top: 20px;
  padding: 0 20px 10px 20px;
}

.sidebar ul li {
  padding: 10px 0;
}

.sidebar nav ul li a {
  color: #b3b3b3;
  text-decoration: none;
  font-weight: 600;
  font-size: 14px;
  font-family: "DM Sans", sans-serif;
}

.sidebar .library {
  margin: 5px 0;
  font-weight: 600;
  font-size: 14px;
}

.sidebar .biblioteca {
  padding-bottom: 10px;
}

.sidebar .biblioteca h2 {
  margin: 10px 0;
  padding-left: 16px;
}

.playlist {
  margin: 10px 10px 0 0;
  background-color: #242424;
  padding: 16px 0 16px 16px;
  border-radius: 8px;
  color: #fff;
}

.playlist h3 {
  padding-bottom: 10px;
  font-size: 12px;
}

.playlist span {
  font-size: 10px;
  padding-bottom: 20px;
}

.playlist span, .cookies span {
  display: block;
}

.playlist button {
  border-radius: 15px;
  border: 0;
  padding: 4px 8px;
  cursor: pointer;
  color: #121212;
  font-weight: 600;
  font-size: 12px;
}

.podcast {
  margin: 10px 10px 0 0;
  background-color: #242424;
  padding: 16px 0 16px 16px;
  border-radius: 8px;
  color: #fff;
}


.podcast button {
  border-radius: 15px;
  border: 0;
  padding: 4px 8px;
  cursor: pointer;
  color: #121212;
  font-weight: 600;
  font-size: 12px;
}

.cookies span {
  font-size: 8px;
  padding: 20px 0;
}

.cookies button {
  border-radius: 15px;
  border: 0.1px solid #fff;
  padding: 5px 15px;
  cursor: pointer;
  color: #fff;
  background-color: #121212;
  font-weight: 600;
  font-size: 12px;
}
